function validateMGSCode(b){const a=/^M[A-Z0-9]{8}$/;return a.test(b)}function decodeMGSCodePartSecure(b){var a=parseInt(b.substring(1),36);return parseInt(a.toString().substring(a.toString().length-4))}function decodeMGSCodePartId(b){var a=parseInt(b.substring(1),36);return parseInt(a.toString().substring(0,a.toString().length-4))}function mgspad(c,a,b){b=b||"0";c=c+"";return c.length>=a?c:new Array(a-c.length+1).join(b)+c}function mgsEncode(c,a){var b=parseInt(c.toString()+mgspad(a,4).toString());return"M"+mgspad(b.toString(36),8).toUpperCase()};